// src/components/CodeRegisterModal.js
import React, { useState } from 'react';
import axios from 'axios';

function CodeRegisterModal({ onClose, onSave }) {
  // TBL_CODE_PARENT 컬럼에 맞춘 상태 변수
  const [pcode, setPcode] = useState('');
  const [pcodeNm, setPcodeNm] = useState('');
  const [pcodeMemo, setPcodeMemo] = useState('');
  const [useTf, setUseTf] = useState('Y'); // 기본값: 사용("Y")

  const handleSave = async () => {
    // 필요한 데이터 검증 로직 추가 가능
    const data = {
      PCODE: pcode,
      PCODE_NM: pcodeNm,
      PCODE_MEMO: pcodeMemo,
      USE_TF: useTf,
    };
    try {
        // 백엔드 URL: http://localhost:4000/api/code
        const response = await axios.post('http://localhost:4000/api/code', data);
        console.log('등록 성공:', response.data);
        onSave(data); // 부모에게 저장 완료 후 처리할 함수 호출 (예: 목록 새로고침)
      } catch (error) {
        console.error('코드 등록 실패:', error);
        // 오류 처리 로직 추가
      }
    onSave(data);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>대분류 코드 등록</h2>
        <div className="form-group">
          <label>코드 (PCODE)</label>
          <input
            type="text"
            value={pcode}
            onChange={(e) => setPcode(e.target.value)}
            placeholder="예: CODE001"
          />
        </div>
        <div className="form-group">
          <label>코드명 (PCODE_NM)</label>
          <input
            type="text"
            value={pcodeNm}
            onChange={(e) => setPcodeNm(e.target.value)}
            placeholder="예: 메인 카테고리"
          />
        </div>
        <div className="form-group">
          <label>코드설명 (PCODE_MEMO)</label>
          <textarea
            value={pcodeMemo}
            onChange={(e) => setPcodeMemo(e.target.value)}
            placeholder="코드에 대한 설명을 입력하세요."
          />
        </div>
        <div className="form-group">
          <label>사용 여부 (USE_TF)</label>
          <div>
            <label>
              <input
                type="radio"
                name="useTf"
                value="Y"
                checked={useTf === 'Y'}
                onChange={() => setUseTf('Y')}
              />
              사용
            </label>
            <label style={{ marginLeft: '10px' }}>
              <input
                type="radio"
                name="useTf"
                value="N"
                checked={useTf === 'N'}
                onChange={() => setUseTf('N')}
              />
              미사용
            </label>
          </div>
        </div>
        <div className="button-group">
          <button onClick={handleSave}>저장</button>
          <button onClick={onClose}>닫기</button>
        </div>
      </div>
    </div>
  );
}

export default CodeRegisterModal;
