// src/pages/Home.js
import React from 'react';

function Home() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  useEffect(() => {
    // 백엔드의 /api/posts 엔드포인트로 GET 요청
    axios.get('/api/posts')
      .then(response => {
        setPosts(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('글 목록 불러오기 에러:', error);
        setLoading(false);
      });
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h1>글 목록</h1>
      {loading ? (
        <p>로딩 중...</p>
      ) : posts.length === 0 ? (
        <p>등록된 글이 없습니다.</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {posts.map((post) => (
            <li key={post.id} style={{ marginBottom: '20px', borderBottom: '1px solid #444', paddingBottom: '10px' }}>
              <h2 style={{ color: '#fff' }}>{post.title}</h2>
              {/* HTML 내용 렌더링 (주의: XSS 취약점에 주의) */}
              <div dangerouslySetInnerHTML={{ __html: post.content_html }} style={{ color: '#ccc' }} />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Home;
